                /*!
                 * @brief TLS FFDHE groups
                 * @tparam Version
                 */
                template<std::size_t Version>
                struct ffdhe_ietf : public field<Version> { };

                template<>
                struct ffdhe_ietf<2048> : public field<2048> {
                    typedef field<2048> policy_type;

                    constexpr static const std::size_t modulus_bits = policy_type::modulus_bits;
                    typedef typename policy_type::integral_type integral_type;

                    typedef typename policy_type::extended_integral_type extended_integral_type;

                    constexpr static const std::size_t number_bits = policy_type::number_bits;

                    constexpr static const integral_type modulus =
                        _cppui2048;

                    typedef typename policy_type::modular_backend modular_backend;
                    constexpr static const modular_params_type modulus_params = modulus;
                    typedef nil::crypto3::multiprecision::number<
                        nil::crypto3::multiprecision::backends::modular_adaptor<
                            modular_backend,
                            nil::crypto3::multiprecision::backends::modular_params_ct<modular_backend, modulus_params>>>
                        modular_type;

                    constexpr static const integral_type mul_generator = 0x02;

                    typedef typename detail::element_fp<params<ffdhe_ietf<modulus_bits>>> value_type;
                    constexpr static const std::size_t value_bits = modulus_bits;

                    constexpr static const std::size_t arity = 1;
                };

